2、测试代码 import ( "io/ioutil" "net/http" "net/http/httptest" "testing")func TestHelloHandler(t *testing.T) &#123; ts := httptest.NewServer(http.HandlerFunc(HelloHandler)) defer ts.Close() res, err := http.Get(ts.URL) if err != nil &#123; t.Fatal(err) &#125; bytes, err := ioutil.ReadAll(res.Body) res.Body.Close() if err != nil &#123; t.Fatal(err) &#125; if string(bytes) != "hello world" &#123; t.Fatal("expected hello world, but got", string(bytes)) &#125;&#125;
