创建jdbc工程，创建包com.voidking.jdbc，新建JdbcMySQL类，内容如下。 package com.voidking.jdbc;import java.sql.*;public class JdbcMySQL &#123; // JDBC driver name and database URL static final String JDBC_DRIVER = "com.mysql.jdbc.Driver"; static final String DB_URL = "jdbc:mysql://localhost/test"; // Database credentials static final String USER = "scott"; static final String PASS = "tiger"; public static void main(String[] args) &#123; Connection conn = null; Statement stmt = null; try &#123; // STEP 2: Register JDBC driver Class.forName(JDBC_DRIVER); // STEP 3: Open a connection System.out.println("Connecting to database..."); conn = DriverManager.getConnection(DB_URL, USER, PASS); // STEP 4: Execute a query System.out.println("Creating statement..."); stmt = conn.createStatement(); String sql; sql = "select id,username,passwd from userbase"; ResultSet rs = stmt.executeQuery(sql); // STEP 5: Extract data from result set while (rs.next()) &#123; // Retrieve by column name int id = rs.getInt("id"); String username = rs.getString("username"); String passwd = rs.getString("passwd"); // Display values System.out.print("ID: " + id); System.out.print(", username: " + username); System.out.println(", passwd: " + passwd); &#125; // STEP 6: Clean-up environment rs.close(); stmt.close(); conn.close(); &#125; catch (SQLException se) &#123; // Handle errors for JDBC se.printStackTrace(); &#125; catch (Exception e) &#123; // Handle errors for Class.forName e.printStackTrace(); &#125; finally &#123; // finally block used to close resources try &#123; if (stmt != null) stmt.close(); &#125; catch (SQLException se2) &#123; &#125;// nothing we can do try &#123; if (conn != null) conn.close(); &#125; catch (SQLException se) &#123; se.printStackTrace(); &#125;// end finally try &#125;// end try System.out.println("Goodbye!"); &#125;// end main&#125;// end JdbcMySQL
