新建Controller在basic/controllers下，新建ProjectController.php，内容如下： &lt;?phpnamespace app\controllers;use Yii;use yii\filters\AccessControl;use yii\web\Controller;use yii\filters\VerbFilter;use app\models\LoginForm;use app\models\ContactForm;use app\models\Project;class ProjectController extends Controller&#123; public function actionAdd($title, $content)&#123; $project = new Project(); $project-&gt;title = $title; $project-&gt;content = $content; $success = $project-&gt;save(); if($success)&#123; $result = array( 'code' =&gt; '0', 'ext' =&gt; 'success' ); echo json_encode($result,JSON_UNESCAPED_UNICODE); &#125; &#125; public function actionEdit($id, $title, $content)&#123; $project = Project::find()-&gt;where(['id'=&gt;$id])-&gt;one(); $project-&gt;title = $title; $project-&gt;content = $content; $success = $project-&gt;save(); if($success)&#123; $result = array( 'code'=&gt; '0', 'ext'=&gt; 'success' ); echo json_encode($result,JSON_UNESCAPED_UNICODE); &#125;else &#123; $result = array( 'code'=&gt; '1', 'ext'=&gt; 'fail' ); echo json_encode($result,JSON_UNESCAPED_UNICODE); &#125; &#125; public function actionDelete($id)&#123; $project = Project::find()-&gt;where(['id'=&gt;$id])-&gt;one(); $success = $project-&gt;delete(); if($success)&#123; $result = array( 'code'=&gt; '0', 'ext'=&gt; 'success' ); echo json_encode($result); &#125;else &#123; $result = array( 'code'=&gt; '1', 'ext'=&gt; 'fail' ); echo json_encode($result); &#125; &#125; public function actionList()&#123; $projectList = Project::find()-&gt;asArray()-&gt;all(); $result = array( 'code'=&gt; '0', 'ext'=&gt; 'success', 'projectList' =&gt; $projectList ); echo json_encode($result,JSON_UNESCAPED_UNICODE); &#125; public function actionPage($pageSize,$pageNum)&#123; $project = new Project(); $total = $project-&gt;find()-&gt;count(); $totalPage = $total%$pageSize ? (int)($total/$pageSize)+1 : (int)($total/$pageSize); $projectList = $project-&gt;find()-&gt;offset(($pageNum-1)*$pageSize)-&gt;limit($pageSize)-&gt;asArray()-&gt;all(); if($projectList)&#123; $resultArr = array( 'totalPage'=&gt; $totalPage, 'pageNum'=&gt; $pageNum, 'projectList'=&gt; $projectList ); $result = array( 'code'=&gt; '0', 'ext'=&gt; 'success', 'obj'=&gt; $resultArr ); echo json_encode($result,JSON_UNESCAPED_UNICODE); &#125; &#125; public function actionFind($id)&#123; $project = new Project(); $item = $project-&gt;find()-&gt;where(['id'=&gt;$id])-&gt;asArray()-&gt;one(); $result = array( 'code'=&gt; '0', 'ext'=&gt; 'success', 'obj'=&gt; $item ); echo json_encode($result,JSON_UNESCAPED_UNICODE); &#125;&#125;
